import * as S from '../styles/MainStyle';
import SearchRec from './SearchRec';
import { useAppSelector } from 'redux/reducer/hooks';
import React from 'react';
import { useDispatch } from 'react-redux';
import { setSearchValue, setOnFocus } from 'redux/reducer/searchSlice';

function Main() {
  const { searchWords, onFocus } = useAppSelector(state => state.searchData);

  const dispatch = useDispatch();

  const onChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    dispatch(setSearchValue(e.currentTarget.value));
    console.log(searchWords);
  };
  const onFocusInput = () => {
    setOnFocus(!onFocus);
  };
  return (
    <S.MainLayout>
      <S.MainTitle>
        국내 모든 임상시험 검색하고 <br /> 온라인으로 참여하기
      </S.MainTitle>
      <S.MainSearchInputBox>
        <S.MainSearchInput
          placeholder="질환명을 입력해 주세요."
          onChange={onChange}
          value={searchWords}
          onFocus={onFocusInput}
        />
        <S.SearchIcon />
      </S.MainSearchInputBox>
      <SearchRec />
    </S.MainLayout>
  );
}

export default Main;
