import * as S from '../styles/MainStyle';
import { useAppSelector } from 'redux/reducer/hooks';

function RecommendWords() {
  const { searchData, searchWords } = useAppSelector(state => state.searchData);

  // 배열의 객제
  if (searchData.length === 0) {
    return <S.recomText> 추천검색어가 없습니다... </S.recomText>;
  }

  return (
    <div>
      {searchData.map(({ sickNm }, idx) => {
        const bold = sickNm.match(searchWords);
        const sick = sickNm.split(searchWords);

        console.log(sick);
        console.log(bold);

        return (
          <>
            {sick.map(() => {
              <S.recomText key={idx}>
                <span>
                  {bold} {sickNm}
                </span>
              </S.recomText>;
            })}
          </>
        );
      })}
    </div>
  );
}

export default RecommendWords;
