import * as S from '../styles/MainStyle';
import SearchRec from './SearchRec';
import Input from 'styles/Input';
import { useDispatch } from 'react-redux';
import { setSearchValue, setOnFocus } from 'redux/reducer/searchSlice';
import { useAppSelector } from 'redux/reducer/hooks';

function Main() {
  const dispatch = useDispatch();
  const { searchWords, isOnFocus } = useAppSelector(state => state.searchData);

  const onChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    dispatch(setSearchValue(e.currentTarget.value));
    console.log(searchWords);
  };
  const onFocusInput = () => {
    setOnFocus(!isOnFocus);
  };
  return (
    <S.MainLayout>
      <S.MainTitle>
        국내 모든 임상시험 검색하고 <br /> 온라인으로 참여하기
      </S.MainTitle>
      <S.MainSearchInputBox>
        <Input onChange={onChange} onFocus={onFocusInput} value={searchWords} />
        <S.SearchIcon />
      </S.MainSearchInputBox>
      <SearchRec />
    </S.MainLayout>
  );
}

export default Main;
