import { useAppSelector } from 'redux/reducer/hooks';

function RecommendWords() {
  const { searchData, searchWords } = useAppSelector(state => state.searchData);

  const newSearch = searchData.map(sick => `${sick.sickNm} 반복`);
  console.log(newSearch);

  // 배열의 객제

  return (
    <div>
      {searchData.map(({ sickNm }, index) => {
        const textArray = sickNm.split(searchWords);
        return (
          <div key={index} className={selectIndex === index ? 'over' : ''}>
            {textArray.map(({ item }, i) => (
              <span key={i} className="search_text">
                {item}
                {i !== textArray.length - 1 && <span className="font_bold">{searchWords}</span>}
              </span>
            ))}
          </div>
        );
      })}
    </div>
  );
}

export default RecommendWords;
