import { useAppDispatch, useAppSelector } from 'redux/reducer/hooks';
import { setSearchValue, setLoading } from '../redux/reducer/searchSlice';
import { AxiosService } from 'api/AxiosService';
import { useEffect } from 'react';

function useAddSearch() {
  const dispatch = useAppDispatch();
  const { searchWords } = useAppSelector(state => state.searchData);
  console.log(searchWords);
  useEffect(() => {
    const onChageWords = async () => {
      dispatch(setLoading(true));
      try {
        const getWords = await AxiosService.getCashSearch(searchWords);
        dispatch(setSearchValue(getWords));
      } catch (err) {
        dispatch(setLoading(false));
        alert(err);
      }
    };
    const timer = () => setTimeout(() => onChageWords(), 500);
    return () => clearTimeout(timer);
  }, [dispatch, searchWords]);
  return searchWords;
}

export default useAddSearch;
